import React from "react";
import { useDispatch } from "react-redux";
import { getTakmiById } from "../../actions/TakimetActions";
import { Link } from "react-router-dom";

function TaskID(props) {
    const { task, repartiId, mjketId, userId } = props;
    const dispatch = useDispatch();

    let priorityAsString;
    let headerClassName;

    const onDeleteTaskClick = (repartiId, mjketId, taskId) => {
        dispatch(deleteTakimi(repartiId, mjketId, taskId));
        const confirmed = window.confirm('A jeni të sigurt që dëshironi të fshini këtë detyrë?');
        if (confirmed) {
            dispatch(getTakmiById(userId)); // Rifresko listën e takimeve
        }
    };

    if (task.priority === 1) {
        headerClassName = "bg-danger text-light";
        priorityAsString = "HIGH";
    } else if (task.priority === 2) {
        headerClassName = "bg-warning text-light";
        priorityAsString = "MEDIUM";
    } else if (task.priority === 3) {
        headerClassName = "bg-info text-light";
        priorityAsString = "LOW";
    }

    return (
        <div className="card mb-4 bg-light">
            <div className={`card-header text-primary ${headerClassName}`}>
                <h3>ID: {task.id} -- Priority: {priorityAsString}</h3>
            </div>
            <div className="card-body">
                <p>{task.description}</p>
                <p>{task.acceptanceCriteria}</p>
                <Link to={`/updateTask/${repartiId}/${mjketId}/${task.id}`} className="btn btn-primary">UPDATE</Link>
                <Link to="#" className="btn btn-danger" onClick={() => onDeleteTaskClick(repartiId, mjketId, task.id)}>DELETE</Link>
            </div>
        </div>
    );
}

export default TaskID;
