import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { useParams } from 'react-router-dom';
import { creaetMjeket } from '../../actions/MjekteAction';

function AddMjket() {
    const { qytetiId, spitaliId ,repartiId } = useParams();
    const dispatch = useDispatch();
    const errors = useSelector((state) => state.errorReducerContent);
    const [mjketData, setMjketData] = useState({
        name: "",
        address:"",
        email:"",
        phoneNumber:""
    });

    const onChange = (event) => {
        const { name, value } = event.target;
        setMjketData({ ...mjketData, [name]: value });
    };

    const onSubmit = (event) => {
        event.preventDefault();
        const newReparti = {
            name: mjketData.name,
            address:mjketData.address,
            email:mjketData.email,
            phoneNumber:mjketData.phoneNumber
        };
        dispatch(creaetMjeket(qytetiId, spitaliId,repartiId , newReparti));
    };

    return(
        <div className="row">
            <div className="col-md-10 m-auto">
                <h1 className="text-center">Create Employee Formrom</h1>
                <hr></hr>
                <form onSubmit={onSubmit} >
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.emri 
            || errors.emri ? " is-invalid" : ""}`}
        placeholder="Employee name"
        name="emri"
        value={spitali.emri}
        onChange={onChange}
        //required
    ></input>
    {errors.emri && <p className="invalid-feedback">{errors.emri}</p>}
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.emri 
            || errors.emri? " is-invalid" : ""}`}
        placeholder="Employee address"
        name="adressa"
        value={spitali.adressa}
        onChange={onChange}
        //required
    ></input>
    {errors.adressa && <p className="invalid-feedback">{errors.adressa}</p>}
    <input
        type="email"
        className={`form-control form-control-lg mt-4${errors.emri ? " is-invalid" : ""}`}
        placeholder="Employee email"
        name="email"
        value={spitali.email}
        onChange={onChange}
        //required
    ></input>
      
    {errors.email && <p className="invalid-feedback">{errors.email}</p>}
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.phoneNumber ? " is-invalid" : ""}`}
        placeholder="Employee phone number"
        name="phoneNumber"
        value={spitali.phoneNumber}
        onChange={onChange}
        required
        
    ></input>
    {errors.phoneNumber && <p className="invalid-feedback">{errors.phoneNumber}</p>}
        <input type="submit" className="btn btn-danger"></input>
    </form>
            </div>
        </div>
    )
}

export default AddMjket;

