import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTakmiById } from "../actions/TakimetActions";
import TaskID from "./Takimet/takimiByID"; // Sigurohuni që të importoni komponentin e duhur

function TakimiByIdList() {
    const dispatch = useDispatch();
    const userId = localStorage.getItem('userId'); // Merrni userID nga localStorage ose nga një tjetër burim

    useEffect(() => {
        if (userId) {
            dispatch(getTakmiById(userId)); // Thirrni veprimin për të marrë takimet bazuar në userID
        }
    }, [dispatch, userId]); // Dependenca për të rifreskuar kur userID ose dispatch ndryshon

    const taskList = useSelector((state) => state.takimReducerContent.takim);
    const isAuthenticated = useSelector((state) => state.authReducerContent.isAuthenticated);

    if (isAuthenticated === null) {
        return <p>Loading...</p>; // Shfaqeni "Loading..." derisa të incializohet autentikimi
    }

    if (isAuthenticated === false) {
        return window.location.href = "/"; // Kthehu në faqen fillestare nëse nuk është autentikuar
    }

    let inputQueue = [];
    let inProgress = [];
    let done = [];

    if (Array.isArray(taskList)) {
        for (const task of taskList) {
            if (task.status === "INPUT QUEUE") {
                inputQueue.push(task);
            } else if (task.status === "DONE") {
                done.push(task);
            } else if (task.status === "IN PROGRESS") {
                inProgress.push(task);
            }
        }
    } else {
        console.log("taskList nuk është një array.");
        // Veprimet e nevojshme për të trajtuar rastin kur taskList nuk është një array
    }

    return (
        <div className='container'>
            <hr></hr>
            <div className='row'>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>INPUT QUEUE</h3>
                        </div>
                        <div className='card-body'>
                            {inputQueue.map((task) => (<TaskID key={task.id} repartiId={task.employee.reparti.id} mjketId={task.employee.id} userId={userId} task={task} />))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>IN PROGRESS</h3>
                        </div>
                        <div className='card-body'>
                            {inProgress.map((task) => (<TaskID key={task.id} repartiId={task.employee.reparti.id} mjketId={task.employee.id} userId={userId} task={task} />))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>DONE</h3>
                        </div>
                        <div className='card-body'>
                            {done.map((task) => (<TaskID key={task.id} repartiId={task.employee.reparti.id} mjketId={task.employee.id} userId={userId} task={task} />))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TakimiByIdList;
