import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import useAuthentication from '../actions/auth';
import TaskID from './Takimet/takimiByID';
import { getTakmiById } from '../actions/TakimetActions';

function TakimiByIdList() {
    const dispatch = useDispatch();
    const userId = localStorage.getItem('userId');
    const [employeeData, setEmployeeData] = useState({});
    const isAuthenticated = useAuthentication();
    
    useEffect(() => {
        if (userId) {
            dispatch(getTakmiById(userId));
        }
    }, [dispatch, userId]);

    const taskList = useSelector((state) => state.takimReducerContent.takims);
    let inputQueue = [];
    let inProgress = [];
    let done = [];

    if (isAuthenticated === null) {
        return <p>Loading...</p>;
    }

    if (isAuthenticated === false) {
        return window.location.href="/";
    }

    if (Array.isArray(taskList)) {
        taskList.forEach((task) => {
            if (task.status === "INPUT QUEUE") {
                inputQueue.push(task);
            } else if (task.status === "DONE") {
                done.push(task);
            } else if (task.status === "IN PROGRESS") {
                inProgress.push(task);
            }
        });
    } else {
        console.log("taskList nuk është një array.");
        // Veprimet e nevojshme për të trajtuar rastin kur taskList nuk është një array
    }

    return (
        <div className='container'>
            <hr></hr>
            <div className='row'>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>INPUT QUEUE</h3>
                        </div>
                        <div className='card-body'>
                            {inputQueue.map((task) => (
                                <TaskID key={task.id} userId={userId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>IN PROGRESS</h3>
                        </div>
                        <div className='card-body'>
                            {inProgress.map((task) => (
                                <TaskID key={task.id} userId={userId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>DONE</h3>
                        </div>
                        <div className='card-body'>
                            {done.map((task) => (
                                <TaskID key={task.id} userId={userId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TakimiByIdList;
