import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUser } from '../../actions/userActions';

const UserProfile = () => {
    const dispatch = useDispatch();
    const userId = useSelector(state => state.auth.userId); // Replace with the correct state path for the user ID
    const userProfile = useSelector(state => state.userReducerContent.user);
    const errors = useSelector(state => state.errorReducerContent);

    useEffect(() => {
        if (userId) {
            dispatch(getUser(userId)); // Dispatch getUser with the userId
        }
    }, [dispatch, userId]);

    return (
        <div>
            <h1>Specializimi i Mjekut</h1>
            {userProfile ? (
                <div>
                    <h1>{userProfile.id}</h1>
                    <h2>Emri: {userProfile.fullName}</h2>
                    <h2>Email: {userProfile.email}</h2>
                    <h2>{userProfile.password}</h2>
                    <h2>Roli: {userProfile.role}</h2>
                    <h2>Mobile: {userProfile.mobile}</h2>
                </div>
            ) : (
                <p>Nuk u gjet asnjë specializim për këtë mjek.</p>
            )}
            {errors && <div className="error">{errors.message}</div>}
        </div>
    );
};

export default UserProfile;



