import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUser } from '../../actions/userActions';
import { Link } from 'react-router-dom';
import profilephoto from '../../images/profilephoto.jpg';
import { getTakmiById } from '../../actions/TakimetActions';

const UserProfile = () => {
    const dispatch = useDispatch();
    const userId = localStorage.getItem('userId');
    const role = localStorage.getItem('role');
    const errors = useSelector(state => state.errorReducerContent);

    useEffect(() => {
        if (userId) {
            dispatch(getUser(userId));
        }
    }, [dispatch, userId]);

    const userProfile = useSelector(state => state.useReducerContent.user);

    const onClick = () => {
        dispatch(getTakmiById(userId));
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column' }}>
            <h1>Profili</h1>
            {userProfile ? (
                <div style={{ border: '1px solid black', padding: '20px', borderRadius: '10px', width: '80%', marginTop: '20px', textAlign: 'center' }}>
                    <img src={profilephoto} alt="Profile" style={{ width: '150px', height: '150px', borderRadius: '50%' }} />
                    <h2>Emri: {userProfile.fullName}</h2>
                    <h2>Email: {userProfile.email}</h2>
                    <h2>Password: {userProfile.password}</h2>
                    <h2>Roli: {userProfile.role}</h2>
                    <h2>Mobile: {userProfile.mobile}</h2>
                </div>
            ) : (
                <p>Nuk u gjet asnjë specializim për këtë mjek.</p>
            )}
            <br />
            {role === "admin" && (
                <Link to={`/takimet/${userProfile.id}`} onClick={onClick} className='btn btn-primary'>Shiko Takimet</Link>
            )}
        </div>
    );
};

export default UserProfile;
