import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { login } from '../SingupAction';
import axios from 'axios';

function Signin() {
  const dispatch = useDispatch();
  const [pacinetData, setPacinetData] = useState({
    email: '',
    password: '',
  });
  const [message, setMessage] = useState('');

  const onChange = (event) => {
    const { name, value } = event.target;
    setPacinetData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    const newPacinet = {
      email: pacinetData.email,
      password: pacinetData.password,
    };

    const response = dispatch(login(newPacinet));

    if (response.status) {
      setMessage(response.message);

      // Verifikimi i tokenit
      const token = response.jwt;
      try {
        const verifyResponse = await axios.post(
          'http://localhost:8083/auth/verifyToken',
          {},
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (verifyResponse.data) {
          window.location.href = '/qytetiList';
        } else {
          setMessage('Token verification failed.');
        }
      } catch (error) {
        setMessage('Token verification failed.');
        console.error('Error during token verification:', error);
      }
    } else {
      setMessage(response.message);
    }
  };

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Create Employee Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className="form-control form-control-lg mt-4"
            placeholder="Employee email"
            name="email"
            value={pacinetData.email}
            onChange={onChange}
            required
          />
          <input
            type="password"
            className="form-control form-control-lg mt-4"
            placeholder="Employee password"
            name="password"
            value={pacinetData.password}
            onChange={onChange}
            required
          />
          <input type="submit" className="btn btn-danger mt-4" value="Create Employee" />
        </form>
        {message && <p>{message}</p>}
      </div>
    </div>
  );
}

export default Signin;


