import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getMjek ,creaetMjeket} from "../../actions/MjekteAction";

function UpdateSpitali() {
  const { qytetiId, spitaliId ,repartiId,mjketId} = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorReducerContent);
  const spitali = useSelector((state) => state.mjeketReducerContetnt.mjekets);

  const [spitaliData, setSpitaliData] = useState({
    id: "",
    name: "",
    address: "",
    email: "",
    phoneNumber: "",
  });

  useEffect(() => {
    dispatch(getMjek(qytetiId,spitaliId,repartiId,mjketId));
  }, [dispatch, qytetiId, spitaliId,repartiId,mjketId]);

  useEffect(() => {
    if (spitali) {
      setSpitaliData({
        id: spitali.id,
        name: spitali.name,
        address: spitali.address,
        email: spitali.email,
        phoneNumber: spitali.phoneNumber,
      });
    }
  }, [spitali]);

  const onChange = (event) => {
    const { name, value } = event.target;
    setSpitaliData((prevSpitaliData) => ({
      ...prevSpitaliData,
      [name]: value,
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updateSpitaliObject = {
      id: spitaliData.id,
      name: spitaliData.name,
      address: spitaliData.address,
      email: spitaliData.email,
      phoneNumber: spitaliData.phoneNumber,
    };
    dispatch(creaetMjeket(qytetiId,spitaliId,repartiId,updateSpitaliObject));
  };

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Update Spitali Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.id ? " is-invalid" : ""
            }`}
            placeholder="Spitali ID"
            name="id"
            value={spitaliData.id}
            disabled
          />
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.name ? " is-invalid" : ""
            }`}
            placeholder="Spitali Name"
            name="name"
            value={spitaliData.emri}
            onChange={onChange}
          />
          {errors.name && <p className="invalid-feedback">{errors.name}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.address ? " is-invalid" : ""
            }`}
            placeholder="Spitali Address"
            name="address"
            value={spitaliData.address}
            onChange={onChange}
          />
          {errors.address && (
            <p className="invalid-feedback">{errors.address}</p>
          )}
          <input
            type="email"
            className={`form-control form-control-lg mt-4${
              errors.email ? " is-invalid" : ""
            }`}
            placeholder="Spitali Email"
            name="email"
            value={spitaliData.email}
            onChange={onChange}
          />
          {errors.email && <p className="invalid-feedback">{errors.email}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.phoneNumber ? " is-invalid" : ""
            }`}
            placeholder="Spitali Phone Number"
            name="phoneNumber"
            value={spitaliData.phoneNumber}
            onChange={onChange}
            required
          />
          {errors.phoneNumber && (
            <p className="invalid-feedback">{errors.phoneNumber}</p>
          )}
          <input type="submit" className="btn btn-danger mt-4" value="Update" />
        </form>
      </div>
    </div>
  );
}

export default UpdateSpitali;
