import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { createReceta } from '../../actions/RecetaAction';

function AddReceta() {
    const {mjekuId } = useParams();
    const dispatch = useDispatch();
    const errors = useSelector((state) => state.errorReducer);

    // Function to get the current date in 'YYYY-MM-DD' format
    const getCurrentDate = () => {
        const today = new Date();
        const year = today.getFullYear();
        const month = String(today.getMonth() + 1).padStart(2, '0');
        const day = String(today.getDate()).padStart(2, '0');
        return `${year}-${month}-${day}`;
    };

    const [recetaData, setRecetaData] = useState({
        dataRecetes: getCurrentDate(), // Set default date
        emriIlacit: "",
        doza: "",
        frekuenca: "",
        kohezgjatja: "",
        verejtje: ""
    });

    const onChange = (event) => {
        const { name, value } = event.target;
        setRecetaData((prevEmployeeData) => ({
            ...prevEmployeeData,
            [name]: value,
        }));
    };
   /* const onSubmit = (event)=>{
        event.preventDefault();
        
       const newEmployee ={
            name:employee.name,
            address:employee.address,
            email:employee.email,
            phoneNumber:employee.phoneNumber
        
        
    }
    dispatch(createEmloyee(departmentId,newEmployee)

    );
     };*/
    const onSubmit = (event) => {
        event.preventDefault();
        dispatch(createReceta(mjekuId, recetaData));
    };

    return (
        <>
            <div className="container d-flex justify-content-center mt-5">
                <div className="col-md-8 m-auto">
                    <h5 className="text-center">Add Receta Form</h5>
                    <hr />
                    <form onSubmit={onSubmit} className='d-flex flex-column'>
                        <div className="form-group">
                            <input
                                type="date"
                                className={`form-control form-control-lg`}
                                placeholder="Data Recetes"
                                name="dataRecetes"
                                value={recetaData.dataRecetes}
                                onChange={onChange}
                            />
                            {errors.dataRecetes && <p className="invalid-feedback">{errors.dataRecetes}</p>}
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.emriIlacit ? 'is-invalid' : ''}`}
                                placeholder="Emri Ilacit"
                                name="emriIlacit"
                                value={recetaData.emriIlacit}
                                onChange={onChange}
                            />
                            {errors.emriIlacit && <p className="invalid-feedback">{errors.emriIlacit}</p>}
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.doza ? 'is-invalid' : ''}`}
                                placeholder="Doza"
                                name="doza"
                                value={recetaData.doza}
                                onChange={onChange}
                            />
                            {errors.doza && <p className="invalid-feedback">{errors.doza}</p>}
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.frekuenca ? 'is-invalid' : ''}`}
                                placeholder="Frekuenca"
                                name="frekuenca"
                                value={recetaData.frekuenca}
                                onChange={onChange}
                            />
                            {errors.frekuenca && <p className="invalid-feedback">{errors.frekuenca}</p>}
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.kohezgjatja ? 'is-invalid' : ''}`}
                                placeholder="Kohezgjatja"
                                name="kohezgjatja"
                                value={recetaData.kohezgjatja}
                                onChange={onChange}
                            />
                            {errors.kohezgjatja && <p className="invalid-feedback">{errors.kohezgjatja}</p>}
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.verejtje ? 'is-invalid' : ''}`}
                                placeholder="Verejtje"
                                name="verejtje"
                                value={recetaData.verejtje}
                                onChange={onChange}
                            />
                            {errors.verejtje && <p className="invalid-feedback">{errors.verejtje}</p>}
                        </div>
                        <input type="submit" className="btn btn-primary btn-block mt-4" value="Add Receta" />
                    </form>
                </div>
            </div>
        </>
    );
}

export default AddReceta;
