import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTask } from '../../actions/TakimetActions';
import { useParams } from 'react-router-dom';

const ShfaqInfoTak = () => {
    const { repartiId, mjketId, takimiId } = useParams();
    const dispatch = useDispatch();
   
    const [employee, setEmployee] = useState(null);
    const [spitali, setSpitali] = useState(null);
    const [qyteti, setQyteti] = useState(null);

    useEffect(() => {
        fetchDataFromRedux();
    }, []);

    const fetchDataFromRedux = async () => {
        if (repartiId && mjketId && takimiId) {
            await dispatch(getTask(repartiId, mjketId, takimiId));
            setIsLoading(false);
        }
    };

    const refreshData = () => {
        fetchDataFromRedux();
    };

    const employeeData = useSelector((state) => state.takimReducerContent?.takim?.employee);
    const spitaliData = employeeData?.reparti?.spitali;
    const qytetiData = spitaliData?.qyteti;

    useEffect(() => {
        // Store data in localStorage if it's not undefined
        if (employeeData) {
            localStorage.setItem('employee', JSON.stringify(employeeData));
        }
        if (spitaliData) {
            localStorage.setItem('spitali', JSON.stringify(spitaliData));
        }
        if (qytetiData) {
            localStorage.setItem('qyteti', JSON.stringify(qytetiData));
        }
    
        // Set state variables
        setEmployee(employeeData);
        setSpitali(spitaliData);
        setQyteti(qytetiData);
    }, [employeeData, spitaliData, qytetiData]);

 

    return (
        <div className="card bg-light mb-3">
            <div className="card-body">
                <div className="employee-info">
                    <h5 className="card-title">Mjeku i Takmit</h5>
                    <button onClick={refreshData}>Refresh Data</button>
                    {employee ? (
                        <div className="employee-details">
                            <h6>Name: {employee.name}</h6>
                            <h6>Address: {employee.address}</h6>
                            <h6>Email: {employee.email}</h6>
                            <h6>Phone Number: {employee.phoneNumber}</h6>
                            {spitali && (
                                <>
                                    <h3>Hospital Information</h3>
                                    <h6>Hospital Name: {spitali.emri}</h6>
                                    <h6>Hospital Address: {spitali.adressa}</h6>
                                    <h6>Hospital Phone Number: {spitali.phoneNumber}</h6>
                                    <h6>Hospital Email: {spitali.email}</h6>
                                </>
                            )}
                            {qyteti && (
                                <>
                                    <h3>City Information</h3>
                                    <h6>City Name: {qyteti.emri}</h6>
                                </>
                            )}
                        </div>
                    ) : (
                        <p>No employee information available.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ShfaqInfoTak;
