import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getRepartis } from '../actions/RepartiActions';
import { verifikimi } from '../actions/SingupAction';

import Reparti from './Reparti/reparti';

function RepartiList() {
    const { qytetiId, spitaliId } = useParams();
    const dispatch = useDispatch();
    const [isAuthenticated, setIsAuthenticated] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setIsAuthenticated(false);
        } else {
            dispatch(verifikimi(token)).then(response => {
                setIsAuthenticated(response.status);
            });
        }
    }, [dispatch]);

    useEffect(() => {
        if (!isAuthenticated) return; // Check authentication before dispatching
        dispatch(getRepartis(qytetiId, spitaliId));
    }, [dispatch, qytetiId, spitaliId, isAuthenticated]); // Added isAuthenticated to dependencies

    const repartilist = useSelector((state) => state.repartiReducerContent.reparts);

    return (
        <div className="container">
            <Link to={`/createRepartiForm/${qytetiId}/${spitaliId}`} className="btn btn-danger">Create Employee</Link>
            <hr />
            {repartilist.map((reparti) => 
                <Reparti spitaliId={spitaliId} key={reparti.id} reparti={reparti} />
            )}
        </div>
    );
}

export default RepartiList;
