import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createQyteti,getQyteti } from '../../actions/QyteteAction';
import { useParams } from 'react-router-dom';

function UpdateQyteti() {
  const dispatch = useDispatch();
  const { id } = useParams();
  const qyteti = useSelector((state) => state.departmentReducerContent.department);
  const errors = useSelector((state) => state.errorReducerContent);
  const [data, setData] = useState({
    id: id,
    name: ''
  });

  useEffect(() => {
    dispatch(getQyteti(id));
    setData((initialState) => ({
      ...initialState,
      id: department.id,
      name: department.name || ''
    }));
  }, [dispatch, id, department.name, department.id]);

  const onChange = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    setData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updateDepartment = {
      id: data.id,
      name: data.name
    };
    dispatch(createQyteti(updateDepartment));
  };

  return (
    <>
      <div className="container d-flex justify-content-center mt-5">
        <div className="col-md-8 m-auto">
          <h5 className="text-center">Update Department Form</h5>
          <hr />
          <form onSubmit={onSubmit} className='d-flex flex-column'>
            <div className="form-group">
              <input
                type="text"
                className="form-control form-control-lg"
                placeholder="Department ID"
                name="departmentId"
                value={id}
                disabled
              />
              <br />
              <input
                type="text"
                className={`form-control form-control-lg ${errors.name ? 'is-invalid' : ''}`}
                placeholder="Department Name"
                name="name"
                value={data.name}
                onChange={onChange}
              />
              {errors.name && <p className="invalid-feedback">{errors.name}</p>}
            </div>
            <input type="submit" className="btn btn-primary btn-block mt-4" />
          </form>
        </div>
      </div>
    </>
  );
}

export default UpdateQyteti;
