import React, { useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getRecetas } from '../actions/RecetaAction';
import Receta from './Receta/receta';
import { verifikimi } from '../actions/SingupAction';
function RecetaList() {
    const { repartiId, mjekuId } = useParams();
    const dispatch = useDispatch();
    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setIsAuthenticated(false);
        } else {
            dispatch(verifikimi(token)).then(response => {
                setIsAuthenticated(response.status);
            });
        }
    }, [dispatch]);

    if (isAuthenticated === null) {
        return <p>Loading...</p>;
    }

    if (isAuthenticated === false) {
        return <p>Unauthorized. Please <Link to="/signin">sign in</Link>.</p>;
    }
    useEffect(() => {
        dispatch(getRecetas(repartiId, mjekuId));
    }, [dispatch, repartiId, mjekuId]);

    const receptailist = useSelector((state) => state.recetetaReducerContent.recetas);

    return (
        <div className="container">
            <Link to={`/addreceta/${mjekuId}`} className="btn btn-danger">
                Create Employee
            </Link>
            <hr />
            {receptailist.map((receta) => (
                <Receta
                    key={receta.id}
                    receta={receta}
                    mjekuId={mjekuId} // Pass qytetiId here
                    repartiId={repartiId} // Pass spitaliId here
                />
            ))}
        </div>
    );
}

export default RecetaList;
