import { GET_REPARTI,GET_REPARTS,CREATE_REPARTI,DELETE_REPARTI } from "../actions/type";
import axios from "axios";
import { GET_ERRORS } from "../actions/type";

export const getMjektets = (repartiId) => async (dispatch) => {
    try {
        const response 
        = await axios.get(`http://localhost:8081/api/mjeket/all/${repartiId}`);
        dispatch({
            type: GET_MJEKETS,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const creaetMjeket = (qytetiId, spitaliId,repartiId,mjeket) => async (dispatch) => {
    try {
        const response = 
        await axios.post(`http://localhost:8081/api/mjeket${qytetiId}/${spitaliId}/${repartiId}`,mjeket);
        window.location.href = `/repartilist/${qytetiId}/${spitaliId}`
        dispatch({
            type: CREATE_REPARTI,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const getMjek = (repartiId,mjektId) => async (dispatch) => {
    try {
        const response =
         await axios.get(`http://localhost:8081/api/mjeket/o/${repartiId}/${mjektId}`);
        dispatch({
            type: GET_REPARTI,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const deletemjkete = ( repartiId,mjektId) => async (dispatch) => {
    try {
        console.log(`Request URL: http://localhost:8081/api/mjeket/${repartiId}/${mjektId}`); // Log request URL
        const response 
        = await axios.delete(`http://localhost:8081/api/mjeket/${repartiId}/${mjektId}`);
        console.log('deleteReparti called with:', {   repartiId ,mjektId});
        dispatch({
            type: DELETE_REPARTI,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};
