import { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getRepartis } from '../actions/RepartiActions';

import { Link } from 'react-router-dom';
import Reparti from './Reparti/reparti';
function RepartiList() {
    const { qytetiId, spitaliId } = useParams();
    const dispatch = useDispatch();
    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setIsAuthenticated(false);
        } else {
            dispatch(verifikimi(token)).then(response => {
                setIsAuthenticated(response.status);
            });
        }
    }, [dispatch]);

    useEffect(() => {
        dispatch(getRepartis(qytetiId, spitaliId));
    }, [dispatch, qytetiId, spitaliId]); // Added dependencies to useEffect
 
    const repartilist = useSelector((state) => state.repartiReducerContent.reparts);

    return (
        <div className="container">
            <Link to={`/createRepartiForm/${qytetiId}/${spitaliId}`}className="btn btn-danger">Create Employee</Link>
            <hr></hr>
             {repartilist.map((reparti) => 
                 <Reparti spitaliId={spitaliId} key={reparti.id} reparti={reparti}/> // Changed id={id} to key={employee.id}
             )}
        </div>
    );
}export default RepartiList
