import { GET_ERRORS } from "../actions/type";
const initialState = {
    errors: null // Vendos një vlerë fillestare për gabime (initial state)
  };
  
  const errorReducerContent = (state = initialState, action) => {
    switch (action.type) {
      case 'GET_ERRORS':
        return {
          ...state,
          errors: action.payload // Vendos gabimet e reja në gjendjen (state)
        };
      default:
        return state; // Kthe gjendjen e mëparshme në raste të tjera
    }
  };
