import React, { useEffect, useState } from 'react';
import Qyteti from './Qytete/qyteti';
import CreateQytetButton from './Qytete/createQytetet';
import { getAllQytetet } from '../actions/QyteteAction';
import { useSelector, useDispatch } from 'react-redux';
import { verifikimi } from '../actions/SingupAction';
import { Link } from 'react-router-dom';

function QytetiList() {
    const dispatch = useDispatch();
    const [alertShown, setAlertShown] = useState(false);
    const [isAuthenticated, setIsAuthenticated] = useState(null);
    const qytetilist = useSelector(state => state.qytetiReducerContent.qytetets);

    // Thirrje për të marrë të gjitha qytetet
    useEffect(() => {
        dispatch(getAllQytetet());
    }, [dispatch]);

    // Verifikimi i token-it të ruajtur
    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setIsAuthenticated(false);
        } else {
            dispatch(verifikimi(token)).then(response => {
                setIsAuthenticated(response.status);
            });
        }
    }, [dispatch]);

    // Shfaq një mesazh "Loading..." derisa të kontrollohet autentifikimi
    if (isAuthenticated === null) {
        return <p>Loading...</p>;
    }

    // Nëse përdoruesi nuk është autentifikuar, shfaq një mesazh dhe një link për login
    if (isAuthenticated === false) {
        return <p>Unauthorized. Please <Link to="/signin">sign in</Link>.</p>;
    }

    // Shfaq një alert për qytetin e fundit të shtuar, vetëm një herë
    useEffect(() => {
        if (qytetilist.length > 0 && !alertShown) {
            const lastQyteti = qytetilist[qytetilist.length - 1];
            const { id, emri } = lastQyteti;
            alert(`Qyteti me ID: ${id} dhe emrin: "${emri}" është shtuar i fundit`);
            setAlertShown(true);
        }
    }, [qytetilist, alertShown]);

    return (
        <div className="container">
            <CreateQytetButton />
            {qytetilist.map((qytet) => (
                <Qyteti key={qytet.id} qytet={qytet} />
            ))}
        </div>
    );
}

export default QytetiList;
