import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import useAuthentication from '../actions/auth';
import Task from './Takimet/takimi';
import { Link } from 'react-router-dom';
import { getTakmiById } from '../actions/TakimetActions';
import TaskID from './Takimet/takimiByID';
function TakimiByIdList() {
    const dispatch = useDispatch();



    const userId= localStorage.getItem('userId');
    useEffect(() => {
        dispatch(getTakmiById(userId));
    }, [dispatch,userId]); // Added dependencies to useEffect
 
    const taskList = useSelector((state) => state.takimReducerContent.takims);
    const [employeeData, setEmployeeData] = useState({});

