              <div className='card text-center'>
                 <div className='card-header bg-primary text-white'>
                        <h3>IN PROGRESS</h3>
                        <div className='card-body'>
{inProgress.map((task) =>(<TaskID key={task.id} repartiId={repartiId} mjketId={mjketId} userId={userId} task={task} />))}
</div>
                </div>
              </div>
            </div>
            <div className='col-md-4'>
              <div className='card text-center'>
                 <div className='card-header bg-primary text-white'>
                        <h3>DONE</h3>
                        <div className='card-body'>
  {done.map((task) =>(<TaskID key={task.id} repartiId={repartiId} mjketId={mjketId} userId={userId} task={task} />))}
