      <div className="col-md-10 m-auto">
        
        <h1 className="text-center ">Login</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className="form-control form-control-lg mt-4"
            placeholder="Employee email"
            name="email"
            value={pacinetData.email}
            onChange={onChange}
            required
          />
          <input
            type="password"
            className="form-control form-control-lg mt-4"
            placeholder="Employee password"
            name="password"
            value={pacinetData.password}
            onChange={onChange}
            required
          />
          <input type="submit" className="btn btn-danger mt-4" value="Login" />
          
          <li className="list-group-item update mr-2">
            <br/>
                            <Link to={"/s"}
                                className="fa fa-edit pr-1 btn btn-primary">Regjistrou
                            </Link>
                        </li>
      
        </form>
        {message && <p>{message}</p>}
      </div>

    </div>*/
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Login</h1>
        <hr />
        <form onSubmit={onSubmit}>
          {/* Email Input */}
          <input
            type="text"
            className="form-control form-control-lg mt-4"
            placeholder="Employee email"
            name="email"
            value={pacinetData.email}
            onChange={onChange}
            required
          />
          {/* Password Input */}
          <input
            type="password"
            className="form-control form-control-lg mt-4"
            placeholder="Employee password"
            name="password"
            value={pacinetData.password}
            onChange={onChange}
            required
          />
          {/* Submit Button */}
          <input
            type="submit"
            className="btn btn-danger mt-4"
            value="Login"
          />
          {/* Registration Link */}
          <li className="list-group-item update mr-2 mt-3">
            <Link to="/register" className="fa fa-edit pr-1 btn btn-primary">
              Regjistrou
            </Link>
          </li>
        </form>
        {/* Message */}
        {message && <p className="mt-3 text-center">{message}</p>}
      </div>
    </div>
  );
}

export default Signin;
