import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { verifikimi } from '../actions/SingupAction';
import Task from './Takimet/takimi';
import { getTakimiList } from '../actions/TakimetActions';

function TaskList() {
    const { repartiId, mjketId } = useParams();
    const [isAuthenticated, setIsAuthenticated] = useState(null);
    const dispatch = useDispatch();
    const taskList = useSelector((state) => state.takimReducerContent.takims);
    let inputQueue = [];
    let inProgress = [];
    let done = [];

    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (!token) {
            setIsAuthenticated(false);
        } else {
            dispatch(verifikimi(token)).then(response => {
                setIsAuthenticated(response.status);
            });
        }
    }, [dispatch]);

    useEffect(() => {
        if (isAuthenticated === true) {
            dispatch(getTakimiList(repartiId, mjketId));
        }
    }, [dispatch, repartiId, mjketId, isAuthenticated]);

    if (isAuthenticated === null) {
        return <p>Loading...</p>;
    }

    if (isAuthenticated === false) {
        return <p>Unauthorized. Please <Link to="/signin">sign in</Link>.</p>;
    }

    if (Array.isArray(taskList)) {
        for (const task of taskList) {
            if (task.status === "INPUT QUEUE") {
                inputQueue.push(task);
            }
            if (task.status === "DONE") {
                done.push(task);
            }
            if (task.status === "IN PROGRESS") {
                inProgress.push(task);
            }
        }
    } else {
        console.log("taskList is not an array.");
        // Handle the case when taskList is not an array
    }

    return (
        <div className='container'>
            <Link to={`/addTask/${repartiId}/${mjketId}`} className='btn btn-danger'>Create Task</Link>
            <hr></hr>
            <div className='row'>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>INPUT QUEUE</h3>
                        </div>
                        <div className='card-body'>
                            {inputQueue.map((task) => (
                                <Task key={task.id} repartiId={repartiId} mjketId={mjketId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>IN PROGRESS</h3>
                        </div>
                        <div className='card-body'>
                            {inProgress.map((task) => (
                                <Task key={task.id} repartiId={repartiId} mjketId={mjketId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
                <div className='col-md-4'>
                    <div className='card text-center'>
                        <div className='card-header bg-primary text-white'>
                            <h3>DONE</h3>
                        </div>
                        <div className='card-body'>
                            {done.map((task) => (
                                <Task key={task.id} repartiId={repartiId} mjketId={mjketId} task={task} />
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TaskList;
