import { CREATE_USER, LOGIN_USER } from "../actions/type";
import axios from "axios";
import { GET_ERRORS } from "../actions/type";

export const createUser = (user) => async (dispatch) => {
    try {
        const response = 
        await axios.post(`http://localhost:8083/auth/signup`,user);
        //window.location.href = `/`
        dispatch({
            type: CREATE_USER,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const login = (user) => async (dispatch) => {
    try {
        const response = 
        await axios.post(`http://localhost:8083/auth/signin`,user);
        //window.location.href = `/`
        dispatch({
            type: CREATE_USER,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};
