import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTask } from '../../actions/TakimetActions';

function ShfaqInfoTak() {
    const [modalOpen, setModalOpen] = useState(false);
    const [selectedTask, setSelectedTask] = useState(null); // Gjendja për të ruajtur informacionin e takimit të zgjedhur
    const dispatch = useDispatch();

    const handleOpenModal = (taskId) => {
        setModalOpen(true);
        setSelectedTask(taskId); // Ruaj takimin e zgjedhur
        dispatch(getTask(taskId)); // Ngarko informacionin për takimin e zgjedhur nga reduksi
    };

    const handleCloseModal = () => {
        setModalOpen(false);
        setSelectedTask(null); // Fshij informacionin e takimit të zgjedhur kur mbyllet modal
    };

    const selectedTaskInfo = useSelector((state) => state.takimReducerContent?.takim);

    return (
        <div className="card bg-light mb-3">
            <div className="card-body">
                <div className="employee-info">
                    <h5 className="card-title">Mjeku i Takmit </h5>
                    <button onClick={() => handleOpenModal(selectedTask)}>Info</button>
                    {modalOpen && (
                        <Modal onClose={handleCloseModal}>
                            {/* Shfaq informacionin e takimit në modal */}
                            {selectedTaskInfo ? (
                                <div className="modal-content">
                                    <h6>Name: {selectedTaskInfo.employee.name}</h6>
                                    <h6>Address: {selectedTaskInfo.employee.address}</h6>
                                    {/* Shto më shumë informacione sipas nevojës */}
                                </div>
                            ) : (
                                <p>Loading...</p>
                            )}
                        </Modal>
                    )}
                </div>
            </div>
        </div>
    );
}

export default ShfaqInfoTak;
