import axios from "axios";
import { GET_SPITALI,GET_SPITALIS,CREATE_SPITALI,DELETE_SPITALI,GET_ERRORS } from "../actions/type";

export const getEmployeeList = (qyteti_id) => async (dispatch) => {
    try {
        const response = await axios.get(`http://localhost:8081/api/spitalet/reparti/${id}`);
        dispatch({
            type: GET_SPITALIS,
            payload: response.data,
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const createEmloyee = (id, spitali) => async (dispatch) => {
    try {
        const response = await axios.post(`http://localhost:8081/api/spitalet/create/${id}`, spitali);
        window.location.href = `/employeeList/${id}`;
        dispatch({
            type: CREATE_SPITALI,
            payload: response.data
        });
    } catch (error) {
        if (error.response && error.response.data) {
            dispatch({
                type: GET_ERRORS,
                payload: error.response.data
            });
        } else {
            console.log("Error response:", error.response);
        }
    }
};

export const getEmployee = (qytetiId,spitaliId) => async (dispatch) => {
    try {
        const response = await axios.get(`http://localhost:8081/api/spitalet/get/${qytetiId}/${spitaliId}`);
        dispatch({
            type: GET_SPITALI,
            payload: response.data
        });
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};

export const deleteEmployee = (qytetiId,spitaliId) => async (dispatch) => {
    try {
        if (window.confirm("Are you sure you want to delete?")) {
            const response = await axios.delete(`http://localhost:8081/api/spitalet/delete/${qytetiId}/${spitaliId}`);
            dispatch({
                type: DELETE_SPITALI,
                payload: response.data
            });
        }
    } catch (error) {
        dispatch({
            type: GET_ERRORS,
            payload: error.response.data
        });
    }
};
