import React, { useState,useEffect  } from "react";
import { useParams } from "react-router-dom";
import { useSelector,useDispatch } from "react-redux";
import { getEmployee, createEmployee } from "../../actions/SpitaliAction";
function UpdateSpitali(){
const {qytetiId} =useParams();
const {spitaliId} =useParams();
const dispatch =useDispatch();
const errors = useSelector((state) => state.errorReducerContent);
const spitali = useSelector((state) => state.spitaliReducerContent.spitali);

const [spitaliData ,setEmployeData] = useState({
     
    id:"",
    emri:"",
    adressa:"",
    email:"",
    phoneNumber:"",
    
});
useEffect(() => {
    dispatch(getEmployee(qytetiId, spitaliId));
},[dispatch,qytetiId,spitaliId]);

useEffect(() => {
        setEmployeData((prevEmployeData) => ({
            ...prevEmployeData,
            id: spitali.id,
            name: spitali.name,
            adressa: spitali.adressa,
            email: spitali.email,
            phoneNumber: spitali.phoneNumber
        }));
}, [spitali]);
    
const onChange = (event) => {
    const { name, value } = event.target;
    setEmployeData((prevEmployeData) => ({
        ...prevEmployeData,
        [name]: value,
    }));
};
const onSubmit= (event) =>{
    event.preventDefault();
    const updateEmloyeObject = {
        id:spitaliData.id,
        emri:spitaliData.name,
        adressa:spitaliData.adressa,
        email:spitaliData.email,
        phoneNumber:spitaliData.phoneNumber

    }
dispatch(createEmployee(qytetiId,updateEmloyeObject));
};
    return(
        <div className="row">
            <div className="col-md-10 m-auto">
                <h1 className="text-center">Create Spitali Formrom</h1>
                <hr></hr>
    <form onSubmit={onSubmit} >
    <input
    type="text"
    className={`form-control form-control-lg mt-4${errors.id || errors.id ? " is-invalid" : ""}`}
    placeholder="Employee ID"
    name="id"
    value={spitaliId}
    disabled
></input>
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.emri 
            || errors.emri ? " is-invalid" : ""}`}
        placeholder="Employee name"
        name="name"
        value={spitaliData.emri}
        onChange={onChange}
    
    ></input>
    {errors.emri && <p className="invalid-feedback">{errors.emri}</p>}
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.adressa 
            || errors.emri? " is-invalid" : ""}`}
        placeholder="Employee address"
        name="adressa"
        value={spitaliData.adressa}
        onChange={onChange}
        //required
    ></input>
    {errors.adressa && <p className="invalid-feedback">{errors.adressa}</p>}
    <input
        type="email"
        className={`form-control form-control-lg mt-4${errors.email ? " is-invalid" : ""}`}
        placeholder="Employee email"
        name="email"
        value={spitaliData.email}
        onChange={onChange}
        //required
    ></input>
      
    {errors.email && <p className="invalid-feedback">{errors.email}</p>}
    <input
        type="text"
        className={`form-control form-control-lg mt-4${errors.phoneNumber ? " is-invalid" : ""}`}
        placeholder="Employee phone number"
        name="phoneNumber"
        value={spitaliData.phoneNumber}
        onChange={onChange}
        required
        
    ></input>
    {errors.phoneNumber && <p className="invalid-feedback">{errors.phoneNumber}</p>}
        <input type="submit" className="btn btn-danger"></input>
    </form>
            </div>
        </div>

    )
}
export default UpdateSpitali;
