import { GET_ERRORS } from "../actions/type";

const initialState = {
    errors: null // Fillimisht nuk ka gabime
};
//aksioni gjithmon e ka nje type dhe paylod type tipin e aksionit 
//ndersa payload qfare ka me kthy
export default function errorReducer(state = initialState, action) {
    switch (action.type) {
        case GET_ERRORS:
            return {
                errors: action.payload // Kthe veprimin (action) në një objekt me gabimet e reja
            };
        default:
            return state; // Kthe gjendjen e mëparshme të gabimeve në rastet tjera
    }
}
