import React, { useState } from 'react';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Validim i thjeshtë për shembull
    const newErrors = {};
    if (!formData.username) newErrors.username = "Username is required";
    if (!formData.email) newErrors.email = "Email is required";
    if (!formData.password) newErrors.password = "Password is required";

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        alert('Registration successful');
        setFormData({
          username: '',
          email: '',
          password: ''
        });
        setErrors({});
      } else {
        alert('Registration failed');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <>
      <div className="container d-flex justify-content-center mt-5">
        <div className="col-md-8 m-auto">
          <h5 className="text-center">Register Form</h5>
          <hr />
          <form onSubmit={handleSubmit} className='d-flex flex-column'>
            <div className="form-group">
              <input
                type="text"
                className={`form-control form-control-lg ${errors.username ? 'is-invalid' : ''}`}
                placeholder="Username"
                name="username"
                value={formData.username}
                onChange={handleChange}
              />
              {errors.username && <p className="invalid-feedback">{errors.username}</p>}
            </div>
            <div className="form-group">
              <input
                type="email"
                className={`form-control form-control-lg ${errors.email ? 'is-invalid' : ''}`}
                placeholder="Email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              {errors.email && <p className="invalid-feedback">{errors.email}</p>}
            </div>
            <div className="form-group">
              <input
                type="password"
                className={`form-control form-control-lg ${errors.password ? 'is-invalid' : ''}`}
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={handleChange}
              />
              {errors.password && <p className="invalid-feedback">{errors.password}</p>}
            </div>
            <input type="submit" className="btn btn-primary btn-block mt-4" value="Register" />
          </form>
        </div>
      </div>
    </>
  );
};

export default Register;
