import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTask } from '../../actions/TakimetActions';
import { useParams } from 'react-router-dom';

const ShfaqInfoTak = () => {
    const { repartiId, mjketId, takimiId } = useParams();
    const dispatch = useDispatch();
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            if (repartiId && mjketId && takimiId) {
                await dispatch(getTask(repartiId, mjketId, takimiId));
                setIsLoading(false);
            }
        };

        fetchData();
    }, [dispatch, repartiId, mjketId, takimiId]);

    const employee = useSelector((state) => state.takimReducerContent?.takim?.employee);
    const spitali = employee?.reparti?.spitali;
    const qyteti = spitali?.qyteti;

    useEffect(() => {
        console.log('employee:', employee);
        console.log('spitali:', spitali);
        console.log('qyteti:', qyteti);
        
        // Store data in localStorage
        localStorage.setItem('employee', JSON.stringify(employee));
        localStorage.setItem('spitali', JSON.stringify(spitali));
        localStorage.setItem('qyteti', JSON.stringify(qyteti));
    }, [employee, spitali, qyteti]);

    useEffect(() => {
        // Retrieve data from localStorage on component mount
        const storedEmployee = localStorage.getItem('employee');
        const storedSpitali = localStorage.getItem('spitali');
        const storedQyteti = localStorage.getItem('qyteti');

        if (storedEmployee && storedSpitali && storedQyteti) {
            // Parse JSON data and update state
            setEmployee(JSON.parse(storedEmployee));
            setSpitali(JSON.parse(storedSpitali));
            setQyteti(JSON.parse(storedQyteti));
        }
    }, []);

    if (isLoading) {
        return <p>Loading...</p>;
    }

    return (
        <div className="card bg-light mb-3">
            <div className="card-body">
                <div className="employee-info">
                    <h5 className="card-title">Mjeku i Takmit</h5>
                    {employee ? (
                        <div className="employee-details">
                            <h6>Name: {employee.name}</h6>
                            <h6>Address: {employee.address}</h6>
                            <h6>Email: {employee.email}</h6>
                            <h6>Phone Number: {employee.phoneNumber}</h6>
                            {spitali && (
                                <>
                                    <h3>Hospital Information</h3>
                                    <h6>Hospital Name: {spitali.emri}</h6>
                                    <h6>Hospital Address: {spitali.adressa}</h6>
                                    <h6>Hospital Phone Number: {spitali.phoneNumber}</h6>
                                    <h6>Hospital Email: {spitali.email}</h6>
                                </>
                            )}
                            {qyteti && (
                                <>
                                    <h3>City Information</h3>
                                    <h6>City Name: {qyteti.emri}</h6>
                                </>
                            )}
                        </div>
                    ) : (
                        <p>No employee information available.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ShfaqInfoTak;
