import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { getMjek } from "../../actions/MjekteAction";

function UpdateMjket() {
  const { qytetiId, spitaliId, repartiId, mjketId } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorReducerContent);
  const mjeket = useSelector((state) => state.mjeketReducerContetnt.mjeket);

  const [mjketData, setMjketData] = useState({
    id: "",
    name: "",
    address: "",
    email: "",
    phoneNumber: "",
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    dispatch(getMjek(qytetiId, spitaliId, repartiId, mjketId));
  }, [dispatch, qytetiId, spitaliId, repartiId, mjketId]);

  useEffect(() => {
    if (mjeket) {
      setMjketData({
        id: mjeket.id || "",
        name: mjeket.name || "",
        address: mjeket.address || "",
        email: mjeket.email || "",
        phoneNumber: mjeket.phoneNumber || "",
      });
      setIsLoading(false);
    }
  }, [mjeket]);

  const onChange = (event) => {
    const { name, value } = event.target;
    setMjketData((prevMjketData) => ({
      ...prevMjketData,
      [name]: value,
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updatedMjketObject = {
      id: mjketData.id,
      name: mjketData.name,
      address: mjketData.address,
      email: mjketData.email,
      phoneNumber: mjketData.phoneNumber,
    };
    dispatch(createMjeket(qytetiId, spitaliId, repartiId, updatedMjketObject));
  };

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Update Doctor Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.id ? " is-invalid" : ""}`}
            placeholder="Doctor ID"
            name="id"
            value={mjketData.id}
            disabled
          />
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.name ? " is-invalid" : ""}`}
            placeholder="Doctor Name"
            name="name"
            value={mjketData.name}
            onChange={onChange}
            required
          />
          {errors.name && <p className="invalid-feedback">{errors.name}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.address ? " is-invalid" : ""}`}
            placeholder="Doctor Address"
            name="address"
            value={mjketData.address}
            onChange={onChange}
            required
          />
          {errors.address && <p className="invalid-feedback">{errors.address}</p>}
          <input
            type="email"
            className={`form-control form-control-lg mt-4${errors.email ? " is-invalid" : ""}`}
            placeholder="Doctor Email"
            name="email"
            value={mjketData.email}
            onChange={onChange}
            required
          />
          {errors.email && <p className="invalid-feedback">{errors.email}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.phoneNumber ? " is-invalid" : ""}`}
            placeholder="Doctor Phone Number"
            name="phoneNumber"
            value={mjketData.phoneNumber}
            onChange={onChange}
            required
          />
          {errors.phoneNumber && <p className="invalid-feedback">{errors.phoneNumber}</p>}
          <input type="submit" className="btn btn-danger mt-4" value="Update Doctor" />
        </form>
      </div>
    </div>
  );
}

export default UpdateMjket;
