import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getMjek, updateMjeket } from "../../actions/MjekteAction";

function UpdateMjket() {
  const { qytetiId, spitaliId, repartiId, mjketId } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorReducerContent);
  const mjek = useSelector((state) => state.mjekReducerContent.mjek);

  const [mjekData, setMjekData] = useState({
    id: "",
    name: "",
    address: "",
    email: "",
    phoneNumber: "",
  });

  useEffect(() => {
    dispatch(getMjek(qytetiId, spitaliId, repartiId, mjketId));
  }, [dispatch, qytetiId, spitaliId, repartiId, mjketId]);

  useEffect(() => {
    if (mjek) {
      setMjekData({
        id: mjek.id || "",
        name: mjek.name || "",
        address: mjek.address || "",
        email: mjek.email || "",
        phoneNumber: mjek.phoneNumber || "",
      });
    }
  }, [mjek]);

  const onChange = (event) => {
    const { name, value } = event.target;
    setMjekData((prevMjekData) => ({
      ...prevMjekData,
      [name]: value,
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updatedMjekObject = {
      id: mjekData.id,
      name: mjekData.name,
      address: mjekData.address,
      email: mjekData.email,
      phoneNumber: mjekData.phoneNumber,
    };
    dispatch(updateMjeket(qytetiId, spitaliId, repartiId, mjketId, updatedMjekObject));
  };

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Update Doctor Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.id ? " is-invalid" : ""}`}
            placeholder="Doctor ID"
            name="id"
            value={mjekData.id}
            disabled
          />
          <input
            type="text"
            className={`form-control form-control-lg mt-4${errors.name ? " is-invalid" : ""}`}
            placeholder="Doctor Name"
            name="name"
            value={mjekData.name}
            onChange={onChange}
            required
          />
          {/* Add more input fields for address, email, phoneNumber if needed */}
          <input type="submit" className="btn btn-danger mt-4" value="Update Doctor" />
        </form>
      </div>
    </div>
  );
}

export default UpdateMjket;
