import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { creaetPacient,getPacinets } from "../../actions/PacinetActions";
import useAuthentication from "../../actions/auth";

function UpdatePacienti() {
  const { qytetiId, spitaliId, repartiId, pacientId } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorReducerContent);
  const pacients = useSelector((state) => state.pacinetReducerContent.pacinets);

  const [pacientData, setPacientData] = useState({
    id: "",
    name: "",
    address: "",
    email: "",
    phoneNumber: "",
    gjinia: "",
  });

  useEffect(() => {
    dispatch(getPacinets(qytetiId, spitaliId, repartiId));
  }, [dispatch, qytetiId, spitaliId, repartiId]);

  useEffect(() => {
    if (pacients && pacients.length > 0) {
      const selectedPacient = pacients.find(pacient => pacient.id === Number(pacientId));
      console.log("Updating pacientData with selectedPacient:", selectedPacient);
      if (selectedPacient) {
        setPacientData({
          id: selectedPacient.id ?? "",
          name: selectedPacient.name ?? "",
          address: selectedPacient.address ?? "",
          email: selectedPacient.email ?? "",
          phoneNumber: selectedPacient.phoneNumber ?? "",
          gjinia: selectedPacient.gjinia ?? "",
        });
      }
    }
  }, [pacientId, pacients]);

  const onChange = (event) => {
    const { name, value } = event.target;
    setPacientData((prevPacientData) => ({
      ...prevPacientData,
      [name]: value,
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updatePacientObject = {
      id: pacientData.id,
      name: pacientData.name,
      address: pacientData.address,
      email: pacientData.email,
      phoneNumber: pacientData.phoneNumber,
      gjinia: pacientData.gjinia,
    };
    dispatch(createPacient(qytetiId, spitaliId, repartiId, updatePacientObject));
  };

  const isAuthenticated = useAuthentication();
  if (isAuthenticated === null) {
    return <p>Loading...</p>;
  }

  if (isAuthenticated === false) {
    return (window.location.href = "/");
  }

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Update Pacient Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className={`form-control form-control-lg mt-4 ${errors.id ? "is-invalid" : ""}`}
            placeholder="Pacient ID"
            name="id"
            value={pacientData.id}
            disabled
          />
          <input
            type="text"
            className={`form-control form-control-lg mt-4 ${errors.name ? "is-invalid" : ""}`}
            placeholder="Pacient Name"
            name="name"
            value={pacientData.name}
            onChange={onChange}
          />
          {errors.name && <p className="invalid-feedback">{errors.name}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4 ${errors.address ? "is-invalid" : ""}`}
            placeholder="Pacient Address"
            name="address"
            value={pacientData.address}
            onChange={onChange}
          />
          {errors.address && <p className="invalid-feedback">{errors.address}</p>}
          <input
            type="email"
            className={`form-control form-control-lg mt-4 ${errors.email ? "is-invalid" : ""}`}
            placeholder="Pacient Email"
            name="email"
            value={pacientData.email}
            onChange={onChange}
          />
          {errors.email && <p className="invalid-feedback">{errors.email}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4 ${errors.phoneNumber ? "is-invalid" : ""}`}
            placeholder="Pacient Phone Number"
            name="phoneNumber"
            value={pacientData.phoneNumber}
            onChange={onChange}
            required
          />
          {errors.phoneNumber && <p className="invalid-feedback">{errors.phoneNumber}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4 ${errors.gjinia ? "is-invalid" : ""}`}
            placeholder="Pacient Gender"
            name="gjinia"
            value={pacientData.gjinia}
            onChange={onChange}
            required
          />
          {errors.gjinia && <p className="invalid-feedback">{errors.gjinia}</p>}
          <input type="submit" className="btn btn-danger mt-4" value="Update" />
        </form>
      </div>
    </div>
  );
}

export default UpdatePacienti;
