import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { createAlergjia } from '../../actions/AlergjiaActions';

function AddAlergjia() {
    const { repartiId, pacinetiId, alergjiaId } = useParams();
    const dispatch = useDispatch();
    const errors = useSelector((state) => state.errorReducerContent);
    const [repartidata, setRepartitData] = useState({
        name: "",
        shkaktari:"",
    });

    const onChange = (event) => {
        const { name, value } = event.target;
        setRepartitData({ ...repartidata, [name]: value });
    };

    const onSubmit = (event) => {
        event.preventDefault();
        dispatch(createAlergjia(repartiId, pacinetiId, alergjiaId));
    };

    return (
        <>
            <div className="container d-flex justify-content-center mt-5">
                <div className="col-md-8 m-auto">
                    <h5 className="text-center">Update Department Form</h5>
                    <hr />
                    <form onSubmit={onSubmit} className='d-flex flex-column'>
                        <div className="form-group">
                           <input
                                type="text"
                                className={`form-control form-control-lg ${errors.name ? 'is-invalid' : ''}`}
                                placeholder="Alergjia Name"
                                name="name"
                                value={repartidata.name}
                                onChange={onChange}
                            />
                            {errors.shkaktari && <p className="invalid-feedback">{errors.shkaktari}</p>}
                            <input
                                type="text"
                                className={`form-control form-control-lg ${errors.shkaktari ? 'is-invalid' : ''}`}
                                placeholder="Shkaktari Name"
                                name="shkaktari"
                                value={repartidata.shkaktari}
                                onChange={onChange}
                            />
                            {errors.shkaktari && <p className="invalid-feedback">{errors.shkaktari}</p>}
                        </div>
                        <input type="submit" className="btn btn-primary btn-block mt-4" value="Update Alergjia" />
                    </form>
                </div>
            </div>
        </>
    );
}



export default AddAlergjia;
