import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { creaetMjeket, getMjek } from "../../actions/MjekteAction";

function UpdateMjket() {
  const { repartiId,mjketId } = useParams();
  const dispatch = useDispatch();
  const errors = useSelector((state) => state.errorReducerContent);
  const mjeket = useSelector((state) => state.mjeketReducerContetnt.mjeket);

  const [mjketdata, setMjketData] = useState({
    id: "",
    emri: "",
    adressa: "",
    email: "",
    phoneNumber: "",
  });

  useEffect(() => {
    dispatch(getMjek(repartiId, mjketId));
  }, [dispatch, repartiId, mjketId]);

  useEffect(() => {
    if (mjeket) {
        setMjketData({
        id: mjeket.id,
        emri: mjeket.emri,
        adressa: mjeket.adressa,
        email: mjeket.email,
        phoneNumber: mjeket.phoneNumber,
      });
    }
  }, [mjeket]);

  const onChange = (event) => {
    const { name, value } = event.target;
    setMjketData((prevSpitaliData) => ({
      ...prevSpitaliData,
      [name]: value,
    }));
  };

  const onSubmit = (event) => {
    event.preventDefault();
    const updateSpitaliObject = {
      id: mjketdata.id,
      emri: mjketdata.emri,
      adressa: mjketdata.adressa,
      email: mjketdata.email,
      phoneNumber: mjketdata.phoneNumber,
    };
    dispatch(creaetMjeket(repartiId, updateSpitaliObject));
  };

  return (
    <div className="row">
      <div className="col-md-10 m-auto">
        <h1 className="text-center">Update Spitali Form</h1>
        <hr />
        <form onSubmit={onSubmit}>
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.id ? " is-invalid" : ""
            }`}
            placeholder="Spitali ID"
            name="id"
            value={mjketdata.id}
            disabled
          />
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.emri ? " is-invalid" : ""
            }`}
            placeholder="Spitali Name"
            name="emri"
            value={mjketdata.emri}
            onChange={onChange}
          />
          {errors.emri && <p className="invalid-feedback">{errors.emri}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.adressa ? " is-invalid" : ""
            }`}
            placeholder="Spitali Address"
            name="adressa"
            value={mjketdata.adressa}
            onChange={onChange}
          />
          {errors.adressa && (
            <p className="invalid-feedback">{errors.adressa}</p>
          )}
          <input
            type="email"
            className={`form-control form-control-lg mt-4${
              errors.email ? " is-invalid" : ""
            }`}
            placeholder="Spitali Email"
            name="email"
            value={mjketdata.email}
            onChange={onChange}
          />
          {errors.email && <p className="invalid-feedback">{errors.email}</p>}
          <input
            type="text"
            className={`form-control form-control-lg mt-4${
              errors.phoneNumber ? " is-invalid" : ""
            }`}
            placeholder="Spitali Phone Number"
            name="phoneNumber"
            value={mjketdata.phoneNumber}
            onChange={onChange}
            required
          />
          {errors.phoneNumber && (
            <p className="invalid-feedback">{errors.phoneNumber}</p>
          )}
          <input type="submit" className="btn btn-danger mt-4" value="Update" />
        </form>
      </div>
    </div>
  );
}

export default UpdateMjket;
