import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { useParams } from 'react-router-dom';
import { creaetMjeket } from '../../actions/MjekteAction';

function AddMjket() {
    const { qytetiId, spitaliId ,repartiId } = useParams();
    const dispatch = useDispatch();
    const errors = useSelector((state) => state.errorReducerContent);
    const [mjketData, setMjketData] = useState({
        name: "",
        address:"",
        email:"",
        phoneNumber:""
    });

    const onChange = (event) => {
        const { name, value } = event.target;
        setMjketData({ ...mjketData, [name]: value });
    };

    const onSubmit = (event) => {
        event.preventDefault();
        const newReparti = {
            name: mjketData.name,
            address:mjketData.address,
            email:mjketData.email,
            phoneNumber:mjketData.phoneNumber
        };
        dispatch(creaetMjeket(qytetiId, spitaliId,repartiId , newReparti));
    };

    return (
        <div className='container d-flex justify-content-center mt-5'>
            <div className='col-md-8'>
                <h5 className='text-center'>Create Reparti Form</h5>
                <hr />
                <form onSubmit={onSubmit} className='d-flex flex-column'>
                    <div className='form-group'>
                        <input
                            type='text'
                            className={`form-control form-control-lg ${errors.name ? "is-invalid" : ""}`}
                            placeholder='Department Name'
                            name='name'
                            value={repartiData.name}
                            onChange={onChange}
                        />
                        {errors.name && <p className='invalid-feedback'>{errors.name}</p>}
                    </div>
                    <button type='submit' className='btn btn-primary btn-block mt-4'>
                        Create Department
                    </button>
                </form>
            </div>
        </div>
    );
}

export default AddReparti;

